import React, { useEffect, useState } from 'react'
import { Navbar, Container, Nav, Card, ListGroup, ListGroupItem } from 'react-bootstrap';
import { Link } from 'react-router-dom';
function Home() {
    const [allProduct, setAllProduct] = useState([])
    useEffect(() => {

        var requestOptions = {
            method: 'POST',
            redirect: 'follow'
        };

        fetch("http://localhost:3001/allProduct", requestOptions)
            .then(response => response.json())
            .then(result => {
                console.log(result)
                setAllProduct(result)

    } )
            .catch (error => console.log('error', error));
}, [])


return (
    <>

        <Navbar bg="primary" variant="dark">
            <Container>
                <Navbar.Brand >Infoware</Navbar.Brand>
                <Nav className="me-auto">
                    <Link to="/view-order" style={{ color: 'white' }}>View Order</Link>

                </Nav>
            </Container>
        </Navbar>

        {
            allProduct.map(data => {
                return (
                    <>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src={data.productimage} />
                            <Card.Body>
                                <Card.Title>{ data.productname}</Card.Title>

                            </Card.Body>
                            <ListGroup className="list-group-flush">
                                <ListGroupItem>{data.productprice}</ListGroupItem>
                            </ListGroup>
                            <Card.Body>
                                <Card.Link href="#">Card Link</Card.Link>
                            </Card.Body>
                        </Card>
                    </>
                )
            })
        }



    </>
)
}

export default Home
