import React, { useEffect, useState } from 'react'
import { Navbar, Container, Nav, Card, ListGroup, ListGroupItem, Button, Col, Row } from 'react-bootstrap';
import { Link , useNavigate} from 'react-router-dom';
function Home({order}) {
    const navigate = useNavigate();
    const [allProduct, setAllProduct] = useState([])
    const [productname, setProductName] = useState()
    const [productprice, setProductPrice] = useState()
    const [productimage, setProductImage] = useState()
    const [username, setUser] = useState()
    useEffect(() => {

        var requestOptions = {
            method: 'POST',
            redirect: 'follow'
        };

        fetch("http://localhost:3001/allProduct", requestOptions)
            .then(response => response.json())
            .then(result => {
                console.log(result)
                setAllProduct(result)

            })
            .catch(error => console.log('error', error));
    }, [])

        const orderPro = (e, data) => {

            e.preventDefault();
            order(data);
            setProductImage(data.productimage)
            setProductName(data.productname)
            setProductPrice(data.productprice)
            setUser(localStorage.getItem('user'))
            const payload = {
                productimage,
                productname,
                productprice,
                username,

            }
            fetch('http://localhost:3001/add-order', {
                method:'POST',
                mode:'cors',
                headers:{
                    "Content-Type" : "application/json"
                },
                body:JSON.stringify(payload)
            }).then(res=>res.json())
            .then((res)=>{
                if(res === 'Place Order'){
                    navigate('/customer-order')
                }else{
                    alert('Not Place Order');
                }
            })
        }

    return (
        <>

            <Navbar bg="primary" variant="dark">
                <Container>
                    <Navbar.Brand >Infoware</Navbar.Brand>
                    <Nav className="me-auto">
                        <Link to="/view-order" style={{ color: 'white' }}>View Order</Link>
                    </Nav>
                </Container>
            </Navbar>
            <Row style={{marginTop:'30px'}}>
            {
                allProduct.map(data => {
                    return (
                        <>


                                <Col lg='3'>

                                    <Card style={{ width: '18rem' }}>
                                        <Card.Img variant="top" src={data.productimage} />
                                        <Card.Body>
                                            <Card.Title>{data.productname}</Card.Title>

                                        </Card.Body>
                                        <ListGroup className="list-group-flush">
                                            <ListGroupItem>{data.productprice}</ListGroupItem>
                                        </ListGroup>
                                        <Card.Body>

                                                <Button variant="primary" type="submit" onClick={(e)=>{orderPro(e,data) }} >
                                                    Buy
                                                </Button>

                                        </Card.Body>
                                    </Card>
                                </Col>




                        </>
                    )
                })
            }
            </Row>


        </>
    )
}

export default Home
