import {Button, Form, Card, ButtonGroup, Navbar, Container, Nav} from 'react-bootstrap';
import { Link } from 'react-router-dom';
function Admin() {


    const [productname,setProductName] = useState()
    const [productprice,setProductPrice] = useState()
    const [prodectimage,setProductImage] = useState()

    const userReg = (e) => {
        // console.log(username+"->"+password);
        e.preventDefault();
        const payload = {
            username:username,
            password:password,
            email:email,
        }
        fetch('http://localhost:3001/register', {
            method:'POST',
            mode:'cors',
            headers:{
                "Content-Type" : "application/json"
            },
            body:JSON.stringify(payload)
        }).then(res=>res.json())
        .then((res)=>{
            if(res === 'Registered Successfully'){
                navigate('/home')
            }else{
                alert('Already Registered');
            }
        })
    }
    return (
        <>
            <Navbar bg="primary" variant="dark">
                <Container>
                    <Navbar.Brand >Admin Page</Navbar.Brand>
                    <Nav className="me-auto">
                        <Link to="/view-order" style={{color: 'white'}}>View Order</Link>

                    </Nav>
                </Container>
            </Navbar>
            <div style={{ justifyContent: 'center', display: 'flex' }}>

                <Card style={{ width: '28rem', margin: '6em' }}>
                    <Card.Body>
                        <Form>
                        <Form.Group className="mb-3" controlId="formBasicEmail" >
                                <Form.Label>Product Name</Form.Label>
                                <Form.Control type="text" placeholder="Enter Product Name" />
                            </Form.Group>


                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Product Price</Form.Label>
                                <Form.Control type="text" placeholder="Enter Product Price" />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Product Image</Form.Label>
                                <Form.Control type="text" placeholder="Product Image Link" />
                            </Form.Group>
                            <Button variant="primary" type="submit" >
                                Add Product
                            </Button>
                        </Form>
                    </Card.Body>
                </Card>
            </div>
        </>
    )
}

export default Admin
